            public Dictionary<string, int> countHelper(IEnumerable<Book> model)
            {
                Dictionary<string, int> unusedBooksCount = new Dictionary<string, int>();
                foreach (var item in model) {
                    if (!unusedBooksCount.ContainsKey(item.Isbn))
                    {
                        unusedBooksCount.Add(item.Isbn, 0);
                        if (item.Taken == false)
                        {
                            unusedBooksCount[item.Isbn] += 1;
                        }
                    }
                }
                return unusedBooksCount;
            }
        

